const pool=require("../config/database"),uploadImageToDatabase=async(o,a,e,s)=>{console.log("Chamada para uploadImageToDatabase");try{const r={cd_evento:o,imagem:a,principal:e,logo_evento:s};console.log("Dados a serem inseridos:",r);const n=await pool.query("INSERT INTO imagem_evento SET ?",r);return console.log("Resultado da inserção:",n),n}catch(o){throw console.error("Erro ao inserir imagem no banco de dados:",o),o}};module.exports={uploadImageToDatabase:uploadImageToDatabase};