const pool=require("../config/database");module.exports={getListaPresenca:(e,n)=>{selectSQL="SELECT presenca_evento.cd_evento, aluno.nome, aluno.rm_aluno\n        FROM ((presenca_evento\n        INNER JOIN evento ON presenca_evento.cd_evento = evento.cd_evento)\n        INNER JOIN aluno ON presenca_evento.rm_aluno = aluno.rm_aluno)\n        WHERE presenca_evento.cd_evento = ?",pool.query(selectSQL,[e],((e,o,t)=>e?n(e):n(null,o)))}};