const pool=require("../config/database");module.exports={create:(e,o)=>{insertSQL="INSERT INTO qrcode_evento(cd_evento, valor_qr) VALUES(?, ?)",pool.query(insertSQL,[e.cd_evento,e.valor_qr],((e,n,t)=>e?o(e):o(null,n)))},getQRCodeValidator:(e,o,n)=>{selectSQL="SELECT presenca_evento.cd_evento, evento.cd_evento, evento.nome_evento, aluno.nome, aluno.rm_aluno, qrcode_evento.valor_qr\n        FROM (((presenca_evento\n        INNER JOIN evento ON presenca_evento.cd_evento = evento.cd_evento)\n        INNER JOIN aluno ON presenca_evento.rm_aluno = aluno.rm_aluno)\n        INNER JOIN qrcode_evento ON presenca_evento.cd_evento = qrcode_evento.cd_evento) \n        WHERE presenca_evento.cd_evento = ? AND presenca_evento.rm_aluno = ?;",pool.query(selectSQL,[e,o],((e,o,t)=>e?n(e):n(null,o[0])))}};